# The website contains a list of blocks grouped by color
# This script will generate a list of blocks for each color
# Each group of blocks is prefaced by a <h2> tag
# Each block in that group is in a <div class="col-xs-12 col-sm-6 col-lg-4 list-of-item">

def get_item_list() -> 'dict[str, list[str]]':
    current_color = None
    final_list = {}

    for line in DATA.splitlines():
        if line.strip().startswith('<h2>'):
            current_color = line[4:-5].split('>')[-2].split('<')[0]
            final_list[current_color] = []
        elif line.strip().startswith('<div class="col-xs-12 col-sm-6 col-lg-4 list-of-item">'):
            n = line[:-13].split("<span>")[-1]
            if n.startswith('<a href="'):
                n = n.split('">')[-1].split('</a>')[0]
            if n.count("Shulker Box") > 0:
                continue
            final_list[current_color].append(n)

    return final_list

import json
