if __name__ == '__main__':
    # read blocks.json
    with open('blocks.json', 'r') as f:
        blocks = json.load(f)

    # get the shortest list of blocks
    shortest = min(blocks, key=lambda x: len(blocks[x]))
    shortest_length = len(blocks[shortest])

    # ask for user input for each other list to select a list of equal length
    for color in blocks:
        if color == shortest:
            continue
        print(f"Select a list of {len(blocks[shortest])} blocks from the following list:")
        for i, block in enumerate(blocks[color]):
            print(f"{i+1}. {block}")
        print()

        picked = blocks[shortest].copy()

        # get user input to remove blocks from the list
        while len(picked) > shortest_length:
            try:
                index = int(input("Enter the number of the block to remove from the list: "))
                if index < 1 or index > len(picked):
                    raise ValueError
                picked.pop(index-1)
            except ValueError:
                print("Invalid input")